
CREATE TABLE IF NOT EXISTS classrooms (
    id varchar(255) not null,
    capacity integer not null,
    name varchar(255),
    primary key (id)
);

CREATE TABLE IF NOT EXISTS classroom_caracteristics (
    classroom_id varchar(255) not null,
    caracteristics varchar(255)
);

CREATE TABLE IF NOT EXISTS courses (
    id varchar(255) not null,
    capacity integer not null,
    capacity_used integer not null,
    location varchar(255),
    name varchar(255),
    shift integer,
    primary key (id)
);

CREATE TABLE IF NOT EXISTS course_curricular_units (
    course_id varchar(255) not null,
    curricular_units varchar(255)
);


CREATE TABLE IF NOT EXISTS curricular_units (
    id varchar(255) not null,
    name varchar(255),
    primary key (id)
);






